 :root {
   --main-color: #ee0081;
   --max-width: 900px;
 }

html, body {
  font-family: 'Roboto', sans-serif;
  margin: 0;
  padding: 0;
  color: #333;
}
body {
  display: flex;
  flex-direction: column;
}
button:focus {
  outline: 0;
}

/** Page header. **/
header {
  background-color: #ee0081;
  color: white;
  font-size: 30px;
  font-weight: 300;
  line-height: 1.45em;
  overflow: hidden;
  padding: 20px 0;
  position: relative;
  text-align: center;
  -webkit-font-smoothing: antialiased;
}
header b {
  color: rgba(255, 255, 255, 1);
  font-weight: 400;
}

/** Loading message. */
#status {
  color: #f8f8f8;
  font-weight: 300;
  margin: 12px 0;
  text-align: center;
}


/** Controls. **/
.controller-panels {
    width: 100%;
    display: flex;
    flex-direction: row;
    margin: 9px auto 0;

}
.panel {
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  flex-grow: 1;
  flex-shrink: 0;
  padding: 1em 1.5em;
  margin: 1em;
}
.panel:first-child {
  background-color: #eee;
  border-radius: 10px;
}
.panel:last-child {}

.panel-row {
  display: flex;
  flex-direction: row;
}
.panel-cell {
    margin-right: 0.5em;
    align-items: center;
    display: flex;
    flex-direction: column;
    flex-grow: 0;
    justify-content: center;
    position: relative;
}
.panel-cell-fill {
  flex-grow: 1;
}
.panel-cell p {
  color: #8b8b8b;
  font-size: 11px;
  margin: 0;
  padding: 0;
  text-align: center;
}
.controller-panels button {
  background: none;
  border: none;
  box-sizing: border-box;
  cursor: pointer;
  margin: 0;
  padding: 0;
}
#train-status {
  width: 124px;
}

/** Training panel. **/
.big-buttons {
  justify-content: space-between;
}
.big-buttons button {
  align-items: center;
  display: flex;
  flex-direction: row;
}
.big-buttons button span {
  border-bottom: 2px solid #484848;
  color: #aaa;
  display: inline-block;
  font-size: 18px;
  font-weight: 500;
  padding: 9px 0;
  text-align: left;
  text-transform: uppercase;
  white-space: nowrap;
}
.params-webcam-row {
  align-items: flex-start;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  margin-top: 35px;
}
.webcam-box-outer {
  border: 1px solid var(--main-color);
  border-radius: 4px;
  box-sizing: border-box;
  display: inline-block;
  padding: 9px;
}
.webcam-box-inner {
  border: 1px solid var(--main-color);
  border-radius: 4px;
  box-sizing: border-box;
  display: flex;
  justify-content: center;
  overflow: hidden;
  width: 250px;
}
#webcam {
  height: 200px;
  transform: scaleX(-1);
}
.hyper-params {
  display: flex;
  flex-direction: column;
  margin-left: 12px;
}
.dropdown {
  flex-direction: column;
  width: 110px;
  margin-bottom: 10px;
}
.dropdown label {
  color: #666;
  font-size: 11px;
  display: block;
  font-weight: 300;
  line-height: 1;
}
.dropdown .select {
  position: relative;
}
.dropdown .select select {
  border-bottom: solid 1px #313131;
  border-radius: 0;
  color: #666;
  display: block;
  font-size: 12px;
  outline: none;
  padding: 6px 0;
  width: 100%;
}

/** Joystick panel. **/
.joystick-panel {
  margin-top: 13px;
}
.panel-cell .help-text {
  font-size: 10px;
  font-style: italic;
  line-height: 1.1;
  margin: 0; padding: 0 0 1em 0;
  text-align: left;
  width: 100%;
}
.panel-row-top .panel-cell-left {

}
.panel-row-middle .panel-cell {
  height: 132px;
}
.thumb-box {
  display: inline-block;
}
.panel-row-middle .thumb-box {
  margin-top: 18px;
}
.thumb-box-outer {
  background: #eee;
  border: 1px solid var(--main-color);
  border-radius: 4px;
  box-sizing: border-box;
  display: inline-block;
  padding: 9px;
  position: relative;
  transition: box-shadow 0.3s;
}
[data-active="_1"] ._1,
[data-active="_2"] ._2,
[data-active="_3"] ._3,
