#copyright {
  color: #f8f8f8;
  font-weight: 300;
  margin: 12px 0;
  text-align: center;
}

#no-webcam {
  display: none;
  text-align: center;
  font-size: 30px;
  padding: 30px;
  line-height: 30px;
}
#current-prediction{
    text-align: center;
    font-size: 2em;
}
