    font-size: 2em !important;
    cursor: pointer;
}
.emoji-picker{
    background: white;
    padding: 1em;
    position: absolute;
    top: 0; left: -150px;
    width: 300px; height: 300px;
    overflow-y: scroll;
    z-index: 99;
    box-shadow: 0 5px 5px #aaa;
}
.emoji-picker a{
    padding: 0.1em;
    display: inline-block;
    text-decoration: none;
}
.emoji-picker a:hover{
    transform: scale(1.1);
    transition: all 0.1s;
}
